//SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

contract eBook {

    address payable public owner;
    uint public pageCount;
    uint public chapterCount;
    string public title;
    string public legalDisclaimer;
    string public openingMessage;
    string public newestVersionHash;
    string public coverImageHash;

    struct page {
        uint pageNumber;
        uint chapterId;
        string content;
    }

    struct chapter {
        uint chapterId;
        uint[] pages;
        string title;
        string description;
    }

    mapping(uint => page) public getPage;
    mapping(uint => chapter) public getChapter;

    constructor() {
        owner = payable(0xBcA3320e93C54513A467Bb517dC25f9Eba15e779);
        title = "The Dark Secret of Humanity";
    }

    function setLegalDisclaimer(string memory _disclaimer) public {
        require(msg.sender == owner);

        legalDisclaimer = _disclaimer;
    }

    function setCoverImageHash(string memory _imageHash) public {
        require(msg.sender == owner);

        coverImageHash = _imageHash;
    }

    function setOpeningMessage(string memory _message) public {
        require(msg.sender == owner);

        openingMessage = _message;
    }

    function setNewestVersionHash(string memory _hash) public {
        require(msg.sender == owner);

        newestVersionHash = _hash;
    }


    function createPage(uint _chapterId, string memory _content) public {

        require(msg.sender == owner);

        pageCount++;
        getPage[pageCount] = page(pageCount, _chapterId, _content);

        chapter storage _chapter = getChapter[_chapterId];
        _chapter.pages.push(pageCount);

    }

    function editPage(uint _pageNum, string memory _content) public {

        require(msg.sender == owner);

        page memory _page = getPage[_pageNum];
        _page.content = _content;
        getPage[_pageNum] = _page;

    }

    function createChapter(string memory _title, string memory _description) public {

        require(msg.sender == owner);

        chapterCount++;
        getChapter[chapterCount] = chapter(chapterCount, new uint[](0), _title, _description);
    }

    function editChapterPages(uint[] memory _pages, uint _chapterId) public {

        require(msg.sender == owner);

        chapter storage _chapter = getChapter[_chapterId];
        delete _chapter.pages;
        for(uint i = 0; i < _pages.length; i++) {
            _chapter.pages.push(_pages[i]);
        }

        getChapter[_chapterId] = _chapter;
    }

    function transferOwnership(address payable _newOwner) private {

        owner = _newOwner;
    }

    function fetchChapter(uint _chapterId) public view returns (chapter memory _chapter) {
        _chapter = getChapter[_chapterId];
    }

}
